package com.leetcode.strings;

/**
 * Created by Dhaval on 8/31/2016.
 */
public class ShortestPalindrome {

    public String findShortestPalindrome(String s) {

        int len = s.length();
        if (len == 1) {
            return s;
        }
        boolean evenFlag = len % 2 == 0;
        int center = len / 2;
        int i = 0;
        while (center > 0) {
            if (evenFlag) {
                if (!expandAroundCenterEven(s, center, center - 1)) {
                    center--;
                    evenFlag = false;
                }
                else {
                    if (i == 0) {
                        return s;
                    }
                    else {
                        return reversString(s.substring(center *  2 , len)) + s;
                    }

                }
            }
            else {
                if(!expandAroundCenterOdd(s, center)) {
                    evenFlag = true;
                }
                else {
                    if (i == 0) {
                        return s;
                    }
                    else {
                        return reversString(s.substring(center *  2 + 1, len)) + s;
                    }
                }
            }
            i++;
        }
        return reversString(s.substring(1, len)) + s;
    }

    private boolean expandAroundCenterEven(String s, int center, int center1) {

        char[] inp = s.toCharArray();
        int p1 = center;
        int p2 = center1;
        return expand(inp, p1, p2);
    }

    private boolean expandAroundCenterOdd(String s, int center) {
        char[] inp = s.toCharArray();
        int p1 = center;
        int p2 = center;
        return expand(inp, p1, p2);
    }

    private boolean expand(char[] inp, int p1, int p2) {

        while (p1 >= 0) {
            if (inp[p1] != inp[p2]) {
                return false;
            }
            p1--;
            p2++;
        }
        return true;
    }

    private String reversString(String s) {
        StringBuilder sb = new StringBuilder(s);
        return sb.reverse().toString();
    }

    public static void main(String[] args) {
        System.out.println(new ShortestPalindrome().findShortestPalindrome(""));
    }
}
